<?php get_header(); ?><div id="content">    <div id="banner">        <div class="flex-nav-container">            <div class="flexslider">                <ul class="slides">                    <?php                    $counter = 1;                    while ($apollo13->get_option('slider_options', 'slider_photo_' . $counter) != ''):                        ?>                        <li>                            <?php if ($apollo13->get_option('slider_options', 'slider_photo_link_' . $counter) != ''): ?>                                <a href="<?php echo $apollo13->get_option('slider_options', 'slider_photo_link_' . $counter); ?>">                            <?php endif; ?>                            <img src="<?php echo $apollo13->get_option('slider_options', 'slider_photo_' . $counter) ?>"/>                            <?php if ($apollo13->get_option('slider_options', 'slider_photo_desc_' . $counter) != ''): ?>                            <p class="flex-caption no_content_font"><?php echo $apollo13->get_option('slider_options', 'slider_photo_desc_' . $counter) ?></p>                            <?php endif; ?>                            <?php if ($apollo13->get_option('slider_options', 'slider_photo_link_' . $counter) != ''): ?>                                </a>                            <?php endif; ?>                        </li>                        <?php $counter++;                    endwhile; ?>                </ul>                <div style="clear: both"></div>            </div>        </div>    </div>    <?php if (strlen($apollo13->get_option('design_options', 'hp_hello_text')) > 0): ?>    <div id="intro_text" class="no_content_font">        <p><?php echo $apollo13->get_option('design_options', 'hp_hello_text') ?></p>    </div>    <?php endif; ?>    <?php $posts_per_page = $apollo13->get_option('design_options', 'hp_latest_works_items_per_page'); ?>    <?php if ($posts_per_page > 0): ?>    <div id="latest_works">        <h2 class="module_title no_content_font"><?php echo __('Latest works', TPL_SLUG) ?></h2>        <div class="flex-nav-container border_top">        <div class="flexslider">        <ul class="slides">            <?php            $open_group = false;            $args = array('post_type' => 'portfolio', 'posts_per_page' => $posts_per_page);            $loop = new WP_Query($args);            $counter = 1;            while ($loop->have_posts()) : $loop->the_post();                ?>                <?php if (($counter - 1) % 4 == 0): ?>                                    <li>                                    <?php endif; ?>                <?php if (($counter - 1) % 2 == 0): ?>                                        <div class="slide_boxs_groups" <?php if ($counter % 4 == 1)                                            echo 'style="margin-right: 16px;"'; ?>>                                            <?php $open_group = true;                endif; ?>                <div <?php if ($counter % 2 == 1)                    echo 'style="margin-right: 16px;"'; ?>  class="four columns slide_box">                    <div class="box_action_starter" onclick="javascript:goToPage(this);" onmouseover="javascript:slideOver(this);" onmouseout="javascript:slideOut(this)"></div>                    <div class="slide_box_hover">                        <div class="slide_box_hover_background"></div>                        <div class="slide_box_content no_content_font">                            <p><?php echo get_the_title(); ?><Br/>                                <span class="extra_description"><?php echo trim(get_post_meta(get_the_ID(), '_project_desc', true)); ?></span>                            </p>                        </div>                    </div>                    <a class="slide_box_link" href="<?php echo get_permalink() ?>"><?php $apollo13->portfolio_get_icon(220, 150); ?></a>                </div>                <?php if (($counter % 2 == 0)): ?>                    <div style="clear: both;"></div>                                        </div>                                    <?php endif; ?>                <?php if ($counter % 4 == 0): ?>                                    </li>                                <?php endif; ?>                <?php if ($counter == $loop->post_count && $counter % 4 != 0): ?>                            </div>                            </li>                    <?php endif; ?>                <?php $counter++;            endwhile; ?>            </ul>        </div>        <div style="clear: both;"></div>    </div>    <?php endif; ?>    <script type="text/javascript">        function slideOver(element) {            jQuery(element).parent().children('.slide_box_hover').fadeIn();        }        function slideOut(element) {            jQuery(element).parent().children('.slide_box_hover').fadeOut();        }        function goToPage(element) {            var link = jQuery(element).parent().children('.slide_box_link').attr('href');            jQuery(location).attr('href', link);        }    </script></div><?php if ($apollo13->get_option('design_options', 'hp_blog_switch') == "on"): ?><div id="latest_blog_posts">    <h2 class="module_title no_content_font"><?php echo __('Latest blog posts', TPL_SLUG) ?></h2>    <div id="latest_blog_posts_container" class="container border_top">        <?php        $counter = 1;        global $more;        $wp_query->query(get_option('posts_per_page') . '&paged=' . $paged);        while ($wp_query->have_posts() && $counter <= 4) : $wp_query->the_post();            ?>            <?php $more = 1; ?>            <div class="four columns latest_blog_post" <?php if ($counter == 4)                echo 'style="margin-right: 0;"'; ?>>                <div class="latest_blog_post_head">                    <a href="<?php the_permalink() ?>"><?php the_post_thumbnail(array(75, 75), array('class' => 'latest_blog_post_img')); ?></a>                    <div class="latest_blog_post_info">                        <a href="<?php the_permalink() ?>"><h3 class="latest_blog_post_topic"><?php the_title(); ?></h3></a>                        <p class="latest_blog_post_date"><?php the_date('d M, Y'); ?></p>                    </div>                    <div style="clear: both"></div>                </div>                <div class="latest_blog_post_content"><p><?php $apollo13->get_excerpt_short(get_the_content(), 18); ?>...</p></div>            </div>            <?php $counter++;        endwhile; ?>        <div style="clear: both"></div>    </div></div><?php endif; ?><?php if ($apollo13->get_option('design_options', 'hp_about_switch') == "on"): ?><?php    $about_page = $apollo13->get_option('design_options', 'about_page');    if ($about_page > 0):        ?>    <div id="about_content">        <?php        $loop = new WP_Query('page_id=' . $about_page);        while ($loop->have_posts()) : $loop->the_post();            ?>            <h2 class="module_title no_content_font"><?php echo get_the_title(); ?></h2>            <div class="container border_top">                <?php echo the_content(); ?>            </div>            <?php endwhile; ?>    </div>    <?php endif; ?><?php endif; ?><?php get_footer(); ?>